def.body:
{% if (K == K2) && (V == V2) %}
        ok = true
        newmap = Map(K, V).new
        each do |k, v|
          newk, newv = yield({k, v})
          newself = assoc(newk, newv) if ok
          newmap = newmap.assoc(newk, newv)
          ok &&= same?(newself)
        end
        ok ? self : newmap
      {% else %}
        to_pf_map { |k, v| yield({k, v}) }
      {% end %}

fmap do |k, v|
  {(  yield k), v}
end

fmap do |k, v|
  {k, (  yield v)}
end
